// import logo from "./logo.svg";
import "./App.css";
import { API } from "./API.js";
import { useState } from "react";

function App() {
  const [search, setSearch] = useState("");
  const [weather, setWeather] = useState({});
  const searchPressed = () => {
    // console.log("Search pressed");
    // console.log(search);
    fetch(
      `${API.base}weather?q=${search}&units=metric&lang=ru&appid=${API.key}`
    )
      .then((res) => res.json())
      .then((result) => {
        // console.log(result);
        setWeather(result);
      });
  };
  return (
    <div className="App">
      <header className="App-header">
        {/* Header */}
        <h1>Weather</h1>
        <div>
          <input
            type="text"
            placeholder="Введите город"
            onChange={(e) => setSearch(e.target.value)}
          />
          <button onClick={searchPressed}>Поиск</button>
        </div>

        {/* Location */}
        <p>{weather.name}</p>
        {/* Temperature */}
        <p>{weather.main.sea_level}</p>
        {/* Condition */}
        <p>Sunny</p>
      </header>
    </div>
  );
}

export default App;
