import search from "./image/search.png";
import axios from "axios";
import wind from "./image/wind.png";
import humidity from "./image/humidity.png";
import "./style.css";
import weather_img from "./image/sunny_clouds.png";
import { useEffect, useState } from "react";
import { API } from "./API.js";
const Home = () => {
  const [data, setData] = useState({
    celcius: 10,
    name_city: "Moscow",
    humidity: 10,
    speed_wind: 4,
  });
  const [name, setName] = useState("");
  // const searchPressed = () => {
  //   // console.log("Search pressed");
  //   console.log(search);
  //   fetch(`${API.base}weather?q=moscow&units=metric&lang=ru&appid=${API.key}`)
  //     .then((res) => res.json())
  //     .then((result) => {
  //       const res_api = result;
  //       console.log(result);
  //       console.log(res_api);
  //     });
  // };

  const handleClick = () => {
    if (name !== "") {
      const urlAPI = `https://api.openweathermap.org/data/2.5/weather?q=${name}&units=metric&lang=ru&appid=a9920ae6206e5c0c4f9d24d14d10e793`;
      axios.get(urlAPI).then((res) => {
        setData({
          ...data,
          celcius: res.data.main.temp,
          name_city: res.data.name,
          humidity: res.data.main.humidity,
          speed_wind: res.data.wind.speed,
        });
      });
    }
  };
  return (
    <div className="container">
      <div className="weather">
        <div className="search">
          <input
            type="text"
            placeholder="Введите название города"
            onChange={(e) => setName(e.target.value)}
          ></input>
          <button className="but_search" onClick={handleClick}>
            <img className="container_search" src={search}></img>
          </button>
        </div>
        <div className="winfo">
          <div className="cont_weather_img">
            <img className="weather_img" src={weather_img}></img>
          </div>

          <h1>{data.celcius}°C</h1>
          <h2>{data.name_city}</h2>
          <div className="details">
            <div className="col">
              <div className="cont_wind">
                <img className="wind_img" src={wind}></img>
                <div>
                  <p>{data.speed_wind} м/с</p>
                  <p>Скорость ветра</p>
                </div>
              </div>
            </div>
            <div className="col">
              <div className="cont_humid">
                <img className="humid_img" src={humidity}></img>
                <div>
                  <p>{data.humidity}%</p>
                  <p>Влажность</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
